( function( $ ) {
	$.fn.scrollFade = function( config ) {
		var defaults = {
			out     : 0,	// 閫忔槑搴�
			duration: 400,	// 鏅傞枔
			hAnime     : 50	// 涓婁笅绉诲嫊璺濋洟
		};
		var config = $.extend( defaults, config );
		var target = this;
		function fade() {
			target.each( function( i, e ) {
				var in_position = $( e ).offset().top + $( window ).height() / 15; // 澶夊寲
				var window_bottom_position = $( window ).scrollTop() + $( window ).height();
				if ( in_position < window_bottom_position ) {
					$( e ).animate( {
						opacity: 1,
						top:0
					}, {
						queue   : false,
						duration: config.duration,
						easing  : 'easeOutQuad'
					} );
				} else {
					if( $( e ).css( 'opacity' ) > config.out ) {
						$( e ).animate( {
							opacity: config.out,
							top: config.hAnime
						}, {
							queue   : false,
							duration: config.duration,
							easing  : 'easeOutQuad'
						} );
					}
				}
			} );
		}
		fade();
		$( window ).scroll( function() {
			fade();
		} );
		$( window ).resize( function() {
			fade();
		} );
		return target;
	};

} )( jQuery );

